import fs from 'fs';
import path from 'path';
const writeFileSync = (path, data) => {
    try {
        fs.writeFileSync(path, data);
    console.log("File written successfully using sync function");
} catch(error){
    console.log("error occured.....");
}
}
const readFileSync = (path) => {
    try {
        const data = fs.readFileSync(path);
        console.log(data);
    } catch(error){
        console.log("error occured.....");
    }
}
const appendFileSync = (path, data) => {
    try {
        fs.appendFileSync(path, data);
    console.log("File appended successfully using sync function");
} catch(error){
    console.log("error occured.....");
}
}

console.log("before write");
writeFilesync("./example.txt", "This is a data written through sync fnction");
console.log("after write");

console.log("before read");
readFileSync("./example.txt");
console.log("after read");

console.log("before append");
appendFileSync("./example.txt", "\nThis is the appended data using sync function:sync function");
console.log("after append");