import fs from 'fs';
const writeFile =async(path,data)=>{
    try{
        fs.writeFile(path,data);
        console.log("File has been written successfully);")
    }catch(error){
        console.log("error occured....");
    }
}
const readFile = async(path)=>{
    try{
        const data = fs.readFile(path,"utf-8");
        console.log(data);
    }catch(error){
        console.log("unable to read....");
    }
}
const appendFile = async(path,data)=>{
    try{
        await fs.appendFile(path,data);
        console.log("Data appended successfully");
    }catch(error){
        console.log("unable to append....");
    }
}

console.log("before write");
writeFile("./example2.txt","This is the data written using async function:'writeFile()'");
console.log("after write");

console.log("before read");
readFile("./example2.txt");
console.log("after read");

console.log("before append");
appendFile("./example2.txt","\nThis is the appended data using async function:'appendFile()'");
console.log("after append");

